// src/components/ConversationList.js
import React from 'react';
import './Inbox.css';

const ConversationList = ({ conversations, onSelect, selectedId }) => {
  return (
    <div className="conversation-list">
      {conversations.length === 0 ? (
        <p className="no-conversations">No conversations found</p>
      ) : (
        conversations.map((c) => (
          <div
            key={c._id}
            className={`conversation-item ${selectedId === c.senderId ? 'selected' : ''}`}
            onClick={() => onSelect(c.senderId)}
          >
            <strong>Sender:</strong> {c.senderId}
            <br />
            <small>Last Msg: {new Date(c.lastMessageTimestamp).toLocaleString()}</small>
          </div>
        ))
      )}
    </div>
  );
};

export default ConversationList;
