// src/components/ConversationList.js
import React from 'react';
import './Inbox.css';

const ConversationList = ({ conversations, onSelect, selectedId }) => {
  return (
    <div className="conversation-list">
      <h3>Inbox</h3>
      {conversations.length === 0 && <p>No conversations found</p>}
      {conversations.map((conv) => (
        <div
          key={conv._id}
          className={`conversation-item ${selectedId === conv._id ? 'active' : ''}`}
          onClick={() => onSelect(conv._id)}
        >
          <div><strong>Sender:</strong> {conv._id}</div>
          <div><strong>Last Msg:</strong> {conv.lastMessage || 'No message'}</div>
          <div><small>{new Date(conv.lastTimestamp).toLocaleString()}</small></div>
        </div>
      ))}
    </div>
  );
};

export default ConversationList;
