import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './FacebookConnect.css'; // import the CSS file

const FacebookConnect = () => {
  const [page, setPage] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchConnectedPage = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/fb/page');
      if (res.data?.page) {
        setPage(res.data.page);
      }
    } catch (err) {
      console.error('Error fetching page info:', err);
    }
  };

  useEffect(() => {
    fetchConnectedPage();
  }, []);

  const handleConnect = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/fb/login-url');
      window.location.href = res.data.url;
    } catch (err) {
      console.error('Error generating login URL:', err);
      alert('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete('http://localhost:5000/api/fb/page');
      setPage(null);
    } catch (err) {
      console.error('Error deleting page:', err);
      alert('Failed to delete Facebook page.');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="fb-container">
      <div className="fb-card">
        {!page ? (
          <>
            <h2 className="fb-heading">Connect Your Facebook Page</h2>
            <button
              onClick={handleConnect}
              className="fb-connect-btn"
              disabled={loading}
            >
              {loading ? 'Redirecting...' : 'Connect Facebook Page'}
            </button>
          </>
        ) : (
          <>
            <h2 className="fb-heading">Facebook Page Connected</h2>
            <p className="fb-label">
              <strong>Page Name:</strong> {page.pageName}
            </p>
            <p className="fb-label">
              <strong>Connected At:</strong> {formatDate(page.connectedAt)}
            </p>
            <button className="fb-delete-btn" onClick={handleDelete}>
              Delete Connection
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default FacebookConnect;
