// src/components/ConversationList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Inbox.css';

const ConversationList = ({ onSelectSender, selectedSenderId }) => {
  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/messages/conversations');
        setConversations(res.data);
      } catch (err) {
        console.error('Failed to fetch conversations:', err);
      }
    };
    fetchConversations();
  }, []);

  return (
    <div className="conversation-list">
      <h3>Conversations</h3>
      {conversations.map((conv) => (
        <div
          key={conv._id}
          className={`conversation-item ${selectedSenderId === conv._id ? 'selected' : ''}`}
          onClick={() => onSelectSender(conv._id)}
        >
          <div className="conversation-name">User: {conv._id}</div>
          <div className="conversation-last">{conv.lastMessage}</div>
        </div>
      ))}
    </div>
  );
};

export default ConversationList;
