import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ConversationList from './ConversationList';
import MessageThread from './MessageThread';
import './Inbox.css';

const Inbox = () => {
  const [conversations, setConversations] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [messages, setMessages] = useState([]);

  // Fetch all conversation list
  const fetchConversations = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/conversations');
      setConversations(res.data || []);
    } catch (err) {
      console.error('Error fetching conversations:', err);
    }
  };

  // Fetch messages when conversation changes
  const fetchMessages = async (convId) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/messages/${convId}`);
      setMessages(res.data || []);
    } catch (err) {
      console.error('Error fetching messages:', err);
    }
  };

  const handleSelect = (id) => {
    setSelectedId(id);
    fetchMessages(id);
  };

  const refreshMessages = () => {
    if (selectedId) fetchMessages(selectedId);
    fetchConversations();
  };

  useEffect(() => {
    fetchConversations();
  }, []);

  return (
    <div className="inbox-container">
      <ConversationList
        conversations={conversations}
        onSelect={handleSelect}
        selectedId={selectedId}
      />

      <div className="message-panel">
        <MessageThread
          messages={messages}
          senderId={selectedId}
          refreshMessages={refreshMessages}
        />
      </div>

      <div className="customer-profile">
        {/* Placeholder for right panel */}
        {selectedId ? (
          <div className="profile-box">
            <h3>Customer Info</h3>
            <p><strong>ID:</strong> {selectedId}</p>
            <p><em>Coming soon: Name, profile, etc.</em></p>
          </div>
        ) : (
          <div className="profile-box">
            <p>Select a conversation to see customer info</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Inbox;
