// components/ConversationList.js
import React from 'react';
import './Inbox.css';

const ConversationList = ({ conversations, onSelect, selectedId }) => {
  return (
    <div className="conversation-list">
      <h3>Inbox</h3>
      {conversations.length === 0 && <p>No conversations found</p>}
      {conversations.map((sender) => (
        <div
          key={sender._id}
          className={`conversation-item ${selectedId === sender._id ? 'active' : ''}`}
          onClick={() => onSelect(sender._id)}
        >
          {sender._id}
        </div>
      ))}
    </div>
  );
};

export default ConversationList;
