// src/components/MessageThread.js
import React from 'react';
import './Inbox.css';

const MessageThread = ({ messages }) => {
  return (
    <div className="message-thread">
      <h2>Messages</h2>
      {messages.length === 0 ? (
        <p className="no-messages">Select a conversation to view messages</p>
      ) : (
        messages.map((msg) => (
          <div key={msg._id} className="message-bubble">
            <p><strong>From:</strong> {msg.senderId}</p>
            <p>{msg.text}</p>
            <small>{new Date(msg.timestamp).toLocaleString()}</small>
          </div>
        ))
      )}
    </div>
  );
};

export default MessageThread;
