import React, { useState } from 'react';
import './Inbox.css';
import axios from 'axios';

const MessageThread = ({ messages }) => {
  const [replyText, setReplyText] = useState('');
  const [sending, setSending] = useState(false);

  const recipientId = messages?.[0]?.senderId; // assuming all messages are from same sender

  const handleSend = async () => {
    if (!replyText.trim() || !recipientId) return;

    try {
      setSending(true);
      await axios.post('http://localhost:5000/api/fb/send', {
        recipientId,
        message: replyText,
      });

      setReplyText('');
      alert('Message sent!');
    } catch (err) {
      console.error(err);
      alert('Failed to send message');
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="message-thread">
      <h3>Messages</h3>
      {messages.length === 0 && <p>Select a conversation to view messages</p>}
      {messages.map((msg, index) => (
        <div key={index} className="message-item">
          <div className="message-text">{msg.text}</div>
          <div className="message-time">{new Date(msg.timestamp).toLocaleString()}</div>
        </div>
      ))}

      {/* Reply box */}
      <div className="reply-box">
        <input
          type="text"
          placeholder="Type your reply..."
          value={replyText}
          onChange={(e) => setReplyText(e.target.value)}
          disabled={sending}
        />
        <button onClick={handleSend} disabled={sending || !replyText.trim()}>
          {sending ? 'Sending...' : 'Send'}
        </button>
      </div>
    </div>
  );
};

export default MessageThread;
