import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FacebookConnect = () => {
  const [loading, setLoading] = useState(false);
  const [pageInfo, setPageInfo] = useState(null); // ✅ Added: to track connected page info

  // ✅ Fetch page info on mount
  useEffect(() => {
    const fetchPage = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/fb/page');
        setPageInfo(res.data);
      } catch (err) {
        setPageInfo(null); // Not connected
      }
    };

    fetchPage();
  }, []);

  // ✅ Connect to Facebook Page
  const handleConnect = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/fb/login-url');
      window.location.href = res.data.url;
    } catch (err) {
      console.error('Error generating login URL:', err);
      alert('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // ✅ Disconnect Page
  const handleDeletePage = async () => {
    try {
      //await axios.delete('http://localhost:5000/api/fb/page');
      setPageInfo(null);
      alert('Page disconnected successfully!');
    } catch (err) {
      console.error('Error deleting page:', err);
      alert('Something went wrong while disconnecting.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h2>Connect Your Facebook Page</h2>

      {pageInfo ? (
        // ✅ Show connected page info + delete option
        <div
          style={{
            padding: '20px',
            backgroundColor: '#f4f4f4',
            borderRadius: '8px',
            display: 'inline-block',
            marginTop: '20px',
          }}
        >
          <p><strong>Page Name:</strong> {pageInfo.pageName}</p>
          <p><strong>Connected At:</strong> {new Date(pageInfo.connectedAt).toLocaleString()}</p>
          <button
            onClick={handleDeletePage}
            style={{
              padding: '10px 20px',
              backgroundColor: '#e53935',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              marginTop: '10px',
              cursor: 'pointer',
            }}
          >
            Delete Connection
          </button>
        </div>
      ) : (
        // ✅ Show connect button if no page is connected
        <button
          onClick={handleConnect}
          style={{
            padding: '12px 24px',
            backgroundColor: '#1877F2',
            color: '#fff',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            cursor: 'pointer',
          }}
          disabled={loading}
        >
          {loading ? 'Redirecting...' : 'Connect Facebook Page'}
        </button>
      )}
    </div>
  );
};

export default FacebookConnect;
